import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function ExpenseDetails({ incomeAmt, expensesAmt, onAddIncome, onAddExpense }) {
  return (
    <div className="container mt-3 p-4 border rounded shadow-sm">
      {/* Balance Section */}
      <div className="text-center mb-3">
        <h4>Your Balance</h4>
        <h2 className="fw-bold">${incomeAmt - expensesAmt}</h2>
      </div>

      {/* Income & Expense Section */}
      <div className="row text-center">
        {/* Income Section */}
        <div className="col-md-6 p-3 border-end">
          <h5>Income</h5>
          <h3 className="text-success fw-bold">${incomeAmt}</h3>
          <button className="btn btn-success mt-2" onClick={onAddIncome}>
             Add Income
          </button>
        </div>

        {/* Expense Section */}
        <div className="col-md-6 p-3">
          <h5>Expense</h5>
          <h3 className="text-danger fw-bold">${expensesAmt}</h3>
          <button className="btn btn-danger mt-2" onClick={onAddExpense}>
            âž– Add Expense
          </button>
        </div>
      </div>
    </div>
  );
}

export default ExpenseDetails;
