import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpensesTable from './ExpensesTable';
import ExpenseTrackerform from './ExpenseTrackerform';
import ExpenseDetails from './ExpenseDetails';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]); // Ensure it's initialized as an array
    const [expensesAmt, setExpensesAmt] = useState(0);
    const [incomeAmt, setIncomeAmt] = useState(0);

    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    useEffect(() => {
        if (Array.isArray(expenses)) {
            const amounts = expenses.map((item) => item.amount || 0);
            console.log('Amounts:', amounts);

            const income = amounts
                .filter(item => item > 0)
                .reduce((acc, item) => acc + item, 0);
            
            console.log('Income:', income);
            setIncomeAmt(income);
        } else {
            console.error("Expenses is not an array:", expenses);
            setExpenses([]); // Reset to an empty array if not valid
        }
    }, [expenses]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    const fetchExpenses = async () => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login');
                return;
            }
            const result = await response.json();
            console.log("Fetched Expenses:", result);

            // Ensure result is an array before updating state
            setExpenses(Array.isArray(result) ? result : []);
            handleSuccess("Expenses loaded successfully");
        } catch (err) {
            handleError(err);
            setExpenses([]); // Reset to an empty array on error
        }
    };

    const addExpenses = async (data) => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                method: 'POST',
                body: JSON.stringify(data)
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login');
                return;
            }
            const result = await response.json();
            console.log("Added Expense:", result);

            // Ensure result is an array before updating state
            setExpenses(Array.isArray(result) ? result : []);
        } catch (err) {
            handleError(err);
        }
    };

    useEffect(() => {
        fetchExpenses();
    }, []);

    return (
        <div>
            <div className='user-section'>
                <h1>Welcome {loggedInUser}</h1>
                <button onClick={handleLogout}>Logout</button>
            </div>
            
            <ExpenseDetails />
            <ExpenseTrackerform addExpenses={addExpenses} />

            {/* Ensure expenses is an array before rendering */}
            {Array.isArray(expenses) && <ExpensesTable expenses={expenses} />}

            <ToastContainer />
        </div>
    );
}

export default Home;
