import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpensesTable from './ExpensesTable';
import ExpenseTrackerForm from './ExpenseTrackerForm';
import ExpenseDetails from './ExpenseDetails';

function Home() {
  const [loggedInUser, setLoggedInUser] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [expensesAmt, setExpensesAmt] = useState(0);
  const [incomeAmt, setIncomeAmt] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    setLoggedInUser(localStorage.getItem('loggedInUser'));
    fetchExpenses();
  }, []);

  // Fetch expenses from API
  const fetchExpenses = async () => {
    try {
      const url = `${APIUrl}/expenses`;
      const headers = {
        headers: {
          'Authorization': localStorage.getItem('token')
        }
      };
      const response = await fetch(url, headers);
      if (response.status === 403) {
        navigate('/login');
        return;
      }
      const result = await response.json();
      console.log("Fetched Expenses:", result);

      setExpenses(result.data || []); // Ensure we set an array
      handleSuccess(result.message);
    } catch (err) {
      handleError(err);
    }
  };

  // Calculate Income and Expenses
  useEffect(() => {
    if (!Array.isArray(expenses) || expenses.length === 0) {
      setIncomeAmt(0);
      setExpensesAmt(0);
      return;
    }

    const amounts = expenses.map((item) => Number(item.amount) || 0);

    const income = amounts.filter(item => item > 0).reduce((acc, item) => acc + item, 0);
    const expense = amounts.filter(item => item < 0).reduce((acc, item) => acc + item, 0) * -1;

    setIncomeAmt(income);
    setExpensesAmt(expense);
  }, [expenses]);

  // Add Expense
  const addExpenses = async (data) => {
    try {
      const url = `${APIUrl}/expenses`;
      const headers = {
        headers: {
          'Authorization': localStorage.getItem('token'),
          'Content-Type': 'application/json'
        },
        method: 'POST',
        body: JSON.stringify(data)
      };
      const response = await fetch(url, headers);
      if (response.status === 403) {
        navigate('/login');
        return;
      }
      const result = await response.json();
      console.log("Added Expense:", result);
      setExpenses(result.data || []);
      handleSuccess(result.message);
    } catch (err) {
      handleError(err);
    }
  };

  // Delete Expense
  const handleDeleteExpense = async (expenseId) => {
    try {
      const url = `${APIUrl}/expenses/${expenseId}`;
      const headers = {
        headers: {
          'Authorization': localStorage.getItem('token'),
          'Content-Type': 'application/json'
        },
        method: 'DELETE',
      };
      const response = await fetch(url, headers);
      if (response.status === 403) {
        navigate('/login');
        return;
      }
      const result = await response.json();
      console.log("Deleted Expense:", result);
      setExpenses(result.data || []);
      handleSuccess(result.message);
    } catch (err) {
      handleError(err);
    }
  };

  return (
    <div>
      <div className='user-section'>
        <h1>Welcome {loggedInUser}</h1>
        <button onClick={() => {
          localStorage.removeItem('token');
          localStorage.removeItem('loggedInUser');
          handleSuccess('User Logged out');
          setTimeout(() => navigate('/login'), 1000);
        }}>
          Logout
        </button>
      </div>
      <ExpenseDetails incomeAmt={incomeAmt} expensesAmt={expensesAmt} />
      <ExpenseTrackerForm addExpenses={addExpenses} />
      <ExpensesTable expenses={expenses} handleDeleteExpense={handleDeleteExpense} />
      <ToastContainer />
    </div>
  );
}

export default Home;
