import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { APIUrl, handleError, handleSuccess } from "../utils";
import { ToastContainer } from "react-toastify";
import ExpensesTable from "./ExpensesTable";
import ExpenseTrackerform from "./ExpenseTrackerform";

function Home() {
    const [loggedInUser, setLoggedInUser] = useState("");
    const [expenses, setExpenses] = useState([]);  
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem("loggedInUser"));
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("loggedInUser");
        handleSuccess("User Logged out");
        setTimeout(() => {
            navigate("/login");
        }, 1000);
    };

    const fetchExpenses = async () => {
        try {
            const url = `${APIUrl}/expenses`;
            const response = await fetch(url, {
                headers: {
                    "Authorization": localStorage.getItem("token"),
                },
            });

            if (response.status === 403) {
                navigate("/login");
                return;
            }

            const result = await response.json();
            console.log("ExpensesTable received:", result);

            if (result.data) {
                setExpenses(result.data); // Ensure you're accessing the correct array
            } else {
                console.error("Expenses data is not an array:", result);
                setExpenses([]);
            }
        } catch (err) {
            handleError(err);
        }
    };

    const addExpenses = async (expenseData) => {
        try {
            const url = `${APIUrl}/expenses`;
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    "Authorization": localStorage.getItem("token"),
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(expenseData),
            });

            if (response.status === 403) {
                navigate("/login");
                return;
            }

            const result = await response.json();
            console.log("Added Expense:", result);

            if (result.data) {
                setExpenses((prev) => [...prev, result.data]);  // Append new expense
            }
        } catch (err) {
            handleError(err);
        }
    };

    useEffect(() => {
        fetchExpenses();
    }, []);

    return (
        <div>
            <div className="user-section">
                <h1>Welcome {loggedInUser}</h1>
                <button onClick={handleLogout}>Logout</button>
            </div>
            <ExpenseTrackerform addExpenses={addExpenses} />
            <ExpensesTable expenses={expenses} />
            <ToastContainer />
        </div>
    );
}

export default Home;
