import React, { useState } from 'react'
import { handleError } from '../utils';

function ExpenseTrackerform(addExpenses) {

    const [expenseInfo,setExpensesInfo]=useState({text:'',amount:''})

    const handleChange = (e) => {
        const { name, value } = e.target;
        console.log(name, value);
        const copyLoginInfo = { ...expenseInfo };
        copyLoginInfo[name] = value;
        setExpensesInfo(copyLoginInfo);
    }

    const handleExpenses=(e)=>{
        e.preventDefault()
        console.log(expenseInfo)
        const {text,amount}=expenseInfo
        if(!text || !amount)
            {
                handleError("All feilds are required")
                return
            } 
            setTimeout(() => {
                
                setExpensesInfo({text:'', amount:''})

            }, 1000);
        addExpenses(expenseInfo)


    }
  return (
    <div className='container'>
    <h1>Login</h1>
    <form onSubmit={handleExpenses}>
        <div>
            <label htmlFor='email'>description</label>
            <input
                onChange={handleChange}
                type='text'
                name='text'
                placeholder='Enter your Expenses Description...'
                value={expenseInfo.text}
            />
        </div>
        <div>
            <label htmlFor='password'>Amount</label>
            <input
                onChange={handleChange}
                type='number'
                name='amount'
                placeholder='Enter your Amount, Expenses(-ve)Income (+ve)...'
                value={expenseInfo.amount}
            />
        </div>
        <button type='submit'>Add Expenses</button>
       
    </form>
</div>
  )
}

export default ExpenseTrackerform
