import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpensesTable from './ExpensesTable';
import ExpenseTrackerform from './ExpenseTrackerform';
import ExpenseDetails from './ExpenseDetails';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [expensesAmt, setExpensesAmt] = useState(0);
    const [incomeAmt, setIncomeAmt] = useState(0);

    const navigate = useNavigate();

    // Load logged-in user from localStorage
    useEffect(() => {
        const user = localStorage.getItem('loggedInUser');
        if (user) {
            setLoggedInUser(user);
        } else {
            navigate('/login'); // Redirect if not logged in
        }
    }, [navigate]);

    // Calculate total income and expenses
    useEffect(() => {
        if (!Array.isArray(expenses)) {
            console.error("Error: expenses is not an array", expenses);
            return;
        }

        const amounts = expenses.map((item) => item.amount || 0);
        console.log("Amounts:", amounts);

        const income = amounts.filter(item => item > 0).reduce((acc, item) => acc + item, 0);
        const expense = amounts.filter(item => item < 0).reduce((acc, item) => acc + item, 0) * -1;

        console.log('Income:', income);
        console.log('Expense:', expense);

        setIncomeAmt(income);
        setExpensesAmt(expense);
    }, [expenses]);

    // Fetch expenses from the API
    const fetchExpenses = async () => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: { 'Authorization': localStorage.getItem('token') }
            };

            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login');
                return;
            }

            const result = await response.json();
            console.log("Fetched expenses:", result);

            setExpenses(Array.isArray(result) ? result : []);
        } catch (err) {
            handleError(err);
        }
    };

    // Add new expense
    const addExpenses = async (data) => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                method: 'POST',
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            };

            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login');
                return;
            }

            const result = await response.json();
            console.log("Added expense:", result);

            setExpenses(Array.isArray(result) ? result : []);
            handleSuccess("Expense added successfully!");
        } catch (err) {
            handleError(err);
        }
    };

    // Delete an expense
    const handleDeleteExpense = async (expenseId) => {
        try {
            const url = `${APIUrl}/expenses/${expenseId}`;
            const headers = {
                method: 'DELETE',
                headers: { 'Authorization': localStorage.getItem('token') }
            };

            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login');
                return;
            }

            const result = await response.json();
            console.log("Deleted expense:", result);

            setExpenses(Array.isArray(result) ? result : []);
            handleSuccess("Expense deleted successfully!");
        } catch (err) {
            handleError(err);
        }
    };

    // Logout function
    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User logged out');

        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    // Fetch expenses on first render
    useEffect(() => {
        fetchExpenses();
    }, []);

    return (
        <div>
            <div className='user-section'>
                <h1>Welcome {loggedInUser}</h1>
                <button onClick={handleLogout}>Logout</button>
            </div>

            {/* Display Income and Expense Summary */}
            <ExpenseDetails incomeAmt={incomeAmt} expensesAmt={expensesAmt} />

            {/* Expense Form */}
            <ExpenseTrackerform addExpenses={addExpenses} />

            {/* Expense Table */}
            <ExpensesTable expenses={expenses} handleDeleteExpense={handleDeleteExpense} />

            <ToastContainer />
        </div>
    );
}

export default Home;
