import React, { useState } from "react";
import { handleError } from "../utils";

function ExpenseTrackerform({ addExpenses }) {
    const [expenseInfo, setExpenseInfo] = useState({ text: "", amount: "" });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setExpenseInfo((prev) => ({ ...prev, [name]: value }));
    };

    const handleExpenses = (e) => {
        e.preventDefault();
        console.log("Expense Submitted:", expenseInfo);

        const { text, amount } = expenseInfo;
        if (!text || !amount) {
            handleError("All fields are required");
            return;
        }

        setTimeout(() => {
            setExpenseInfo({ text: "", amount: "" });
        }, 1000);

        if (typeof addExpenses === "function") {
            addExpenses(expenseInfo);
        } else {
            console.error("addExpenses is not a function");
        }
    };

    return (
        <div className="container">
            <h1>Add Expense</h1>
            <form onSubmit={handleExpenses}>
                <div>
                    <label>Description</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        name="text"
                        placeholder="Enter your Expense Description..."
                        value={expenseInfo.text}
                    />
                </div>
                <div>
                    <label>Amount</label>
                    <input
                        onChange={handleChange}
                        type="number"
                        name="amount"
                        placeholder="Enter Amount (Expense -ve, Income +ve)..."
                        value={expenseInfo.amount}
                    />
                </div>
                <button type="submit">Add Expense</button>
            </form>
        </div>
    );
}

export default ExpenseTrackerform;
